import R from 'ramda';

//compare taken *id* from array ids with *id* from array phones and return phone
const getPhoneById = (state, id) => R.prop(id, state.phones);

export const getPhones = state => {
    //here we take all phones for page
    const phones = R.map(id => getPhoneById(state, id), state.phonesPage.ids);
    return phones
};

//return the quantity of rendered products
export const getRenderedPhonesLength = state => R.length(state.phonesPage.ids);